import { Component, OnInit } from '@angular/core';
import { SpotifyService } from '../services/spotify.service';
import { Song } from '../models/song';
import { Album } from '../models/album';

@Component({
  selector: 'app-songs',
  templateUrl: './songs.component.html',
  styleUrls: ['./songs.component.css']
})
export class SongsComponent implements OnInit {

  songs? : Song[];
  albumName? : string;
  album? : Album;

  constructor(public spotify : SpotifyService, public route : ActivatedRoute) { }

  async ngOnInit() {
    await this.spotify.connect();
    this.albumName = this.route.snapshot.paramMap.get("artistName");
    this.album = await this.spotify.searchAlbum(this.albumName!);
  }

  async getSongs() : Promise<void>{
    this.songs = await this.spotify.getSongs(this.album!);
  }

}
